settings.custom_liquid:
{% assign user_manual_value = product.metafields.sf_products.manual %}
              {% assign user_manual_french_value = product.metafields.sf_products.manual_french %}
              {% assign user_manual_spanish_value = product.metafields.sf_products.manual_spanish %}

              {% if user_manual_value %}
                <a class="" href="{{ user_manual_value }}" target="new">Download Manual (en)</a><br>
              {% endif %}

              {% if user_manual_french_value %}
                <a class="" href="{{ user_manual_french_value }}" target="new">Download Manual (fr)</a><br>
              {% endif %}

              {% if user_manual_spanish_value %}
                <a class="" href="{{ user_manual_spanish_value }}" target="new">Download Manual (es)</a>
              {% endif %}

settings.liquid_content:
{% assign retail_package_contents_value = product.metafields.sf_products.retail_package_contents %}
          {% if retail_package_contents_value %}
<h3>Contents</h3>
             {{ retail_package_contents_value }}

          {% endif %}

          {% assign assembled_width = product.metafields.sf_products.assembled_width %}
          {% assign assembled_height = product.metafields.sf_products.assembled_height %}
          {% assign assembled_depth = product.metafields.sf_products.assembled_depth %}
          {% if assembled_width and assembled_height and assembled_depth %}
            <div class="flex-item">
              <h3>Product Dimensions</h3>
              <p>{{ assembled_width }} x {{ assembled_height }} x {{ assembled_depth }} in</p>
            </div>
          {% endif %}
          {% assign folded_dimensions = product.metafields.sf_products.folded_dimensions %}
          {% if folded_dimensions %}
            <div class="flex-item">
              <h3>Folded Dimensions</h3>
              <p>{{ folded_dimensions }}</p>
            </div>
          {% endif %}
          {% assign package_width = product.metafields.sf_shipping.package_width %}
          {% assign package_height = product.metafields.sf_shipping.package_height %}
          {% assign package_depth = product.metafields.sf_shipping.package_depth %}
          {% if package_width and package_height and package_depth %}
            <div class="flex-item">
              <h3>Package Dimensions</h3>
              <p>{{ package_width }} x {{ package_height }} x {{ package_depth }} in</p>
            </div>
          {% endif %}

          {% assign assembled_weight = product.metafields.sf_products.assembled_weight %}
          {% if assembled_weight %}
            <div class="flex-item">
              <h3>Product Weight</h3>
              <p>{{ assembled_weight }} lbs.</p>
            </div>
          {% endif %}
          {% assign package_weight = product.metafields.sf_shipping.package_weight %}
          {% if package_weight %}
            <div class="flex-item">
              <h3>Package Weight</h3>
              <p>{{ package_weight }} lbs.</p>
            </div>
          {% endif %}

          {% assign carrier_weight_without_base = product.metafields.sf_car_seat.carrier_weight_without_base %}
          {% if carrier_weight_without_base %}
            <div class="flex-item">
              <h3>Weight w/o Base</h3>
              <p>{{ carrier_weight_without_base }} lbs.</p>
            </div>
          {% endif %}

          <!-- optional section -->
          {% assign harness_rethread_method = product.metafields.sf_products.harness_rethread_method %}
          {% assign harness_type = product.metafields.sf_products.harness_type %}
          {% assign bubble_level = product.metafields.sf_car_seat.bubble_level %}
          {% assign harness_covers = product.metafields.sf_car_seat.harness_covers %}
          {% assign latch_install = product.metafields.sf_car_seat.latch_install %}
          {% assign maturity = product.metafields.sf_products.maturity %}

          {% if harness_rethread_method or harness_type %}
            {% if harness_type %}
              <div class="flex-item">
                <h3>Harness Type</h3>
                <p>
                  {{ harness_type }}
                </p>
              </div>
            {% endif %}
            {% if harness_rethread_method %}
              <div class="flex-item">
                <h3>Harness Rethread</h3>
                <p>
                  {{ harness_rethread_method }}
                </p>
              </div>
            {% endif %}
          {% endif %}
          {% if bubble_level or harness_covers or latch_install %}
            {% if bubble_level == 1 %}
              <div class="flex-item">
                <h3>Bubble Level</h3>
                <p>Yes</p>
              </div>
            {% endif %}
            {% if harness_covers == 1 %}
              <div class="flex-item">
                <h3>Harness Covers</h3>
                <p>Yes</p>
              </div>
            {% endif %}
            {% if latch_installs == 1 %}
              <div class="flex-item">
                <h3>Latch Install</h3>
                <p>Yes</p>
              </div>
            {% endif %}
          {% endif %}

          <div class="flex-item">
            <h3>Assembly Required</h3>
            {% assign assembly_switch_spec = product.metafields.sf_products.assembly_required %}
            {% if assembly_switch_spec == 1 %}
              <p>Yes</p>
            {% else %}
              <p>No</p>
            {% endif %}
          </div>
          <div class="flex-item">
            {% assign my_switch = product.metafields.sf_gates.hardware_included %}
            <h3>Hardware Included</h3>
            {% if my_switch == 1 %}
              <p>Yes</p>
            {% else %}
              <p>No</p>
            {% endif %}
          </div>

{% assign care_copy = product.metafields.sf_products.care_copy | split: '|' %}
      {% if care_copy %}
<h3>Care</h3>
        <ul>
          {% for li in care_copy %}
            <li>{{ li }}</li>
          {% endfor %}
        </ul>
      {% endif %}
<br>
      <h3>Materials</h3>
      {% assign material = product.metafields.sf_products.material %}
      {% if material %}
        <p>{{ material }}</p>
      {% endif %}
<br>

{% assign specifications_image = product.metafields.sf_products.specifications_image %}
        {% if specifications_image %}
          <image loading="lazy" alt="{{ product.title }} Specifications" src="{{ specifications_image }}">
        {% endif %}
